import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import { CartContext } from '../../Context/cartContext'
import { CgShoppingCart } from 'react-icons/cg'
import { Badge } from 'react-bootstrap'



export const CartIcon = () => {

    //const { cartSize } = React.useContext(CartContext);


    const [cartProducts] = useContext(CartContext);

    const cartSize = () => {
         console.log('estoy en cart size');
         return cartProducts.reduce((sum, product) => sum + product.count, 0);
    }

    return (
        <div classname="container">
            <Link to={'/cart'}>
                {/*<CgShoppingCart>
                    <Badge variant="light"> {cartSize()} </Badge>
                </CgShoppingCart>*/}


                 <CgShoppingCart />
                <span className="badge white"> {cartSize()} </span> 


            </Link>
        </div>
    );
};
