import React from 'react';
import ItemCountContainer from '../../Containers/ItemCountContainer'
import AddToCart from '../AddToCart/AddToCart';
import { CartContext } from '../../Context/cartContext'
import ItemDetailContainer from '../../Containers/ItemDetailContainer'
import ItemCount from '../ItemCount/ItemCount';
import { Button } from 'react-bootstrap';



const ItemDetail = ({ product }) => {
    const [count, setCount] = React.useState(1)
    const { productsAdd } = React.useContext(CartContext)

    const handleClickComprar = () => {
        if (count > 0) {
            productsAdd({
                id: product.id,
                title: product.title,
                image: product.image,
                count,
                price: product.price
            })
        }
    }

    return (
        <div className="container">
            <ItemCount setCount={setCount} count={count} min={1} max={30} />
            <Button onClick={handleClickComprar}>Comprar</Button>
            <p>Precio: $ {Intl.NumberFormat().format(product.price)}</p>
            <p>Stock disponible: {product.stock}</p>
            <div className="row">
                <div className="col-sm">
                    <h3>{product.title}</h3>
                </div>
                <div className="col-sm">
                    <img src={product.img} alt="Foto del producto"></img>
                </div>
                <div className="col-sm">
                    <ItemCountContainer setCount={setCount} count={count} min={1} max={30} />
                </div>
                <div>
                    <p>Precio: $ {Intl.NumberFormat().format(product.price)}</p>
                    <p>Stock disponible: {product.stock}</p>
                </div>
                <AddToCart />
            </div>
        </div>
    );
}

export default ItemDetail;
