import React from 'react';
import ItemCountContainer from '../../Containers/ItemCountContainer'
import AddToCart from '../AddToCart/AddToCart';
import { CartContext } from '../../Context/cartContext'
import ItemDetailContainer from '../../Containers/ItemDetailContainer'
import ItemCount from '../ItemCount/ItemCount';
import { Button } from 'react-bootstrap';



const ItemDetail = ({ product, itemqty, addtocart }) => {
    //const [count, setCount] = React.useState(1)
    // const { productsAdd } = React.useContext(CartContext)

    // const handleClickComprar = () => {
    //     if (count > 0) {
    //         productsAdd({
    //             id: product.id,
    //             title: product.title,
    //             image: product.image,
    //             count,
    //             price: product.price
    //         })
    //     }
    // }
    const [cartProducts, setCartProducts] = React.useContext(CartContext);

    const handleClick = () => {
        let cartItem = {
            id: product.id,
            title: product.title,
            price: product.price,
            // count: qtyCounter
            count: itemqty
        }
        if (cartProducts.find((product) => product.id === cartItem.id)) {
            const newCartProducts = cartProducts.map((product) => {
                if (product.id === cartItem.id) {
                    return { ...product, count: cartItem.count + product.count }
                }
                return product
            })
            setCartProducts(newCartProducts)
        } else {
            setCartProducts([...cartProducts, cartItem])
        }
    }

    return (
        <div className="container">
            {/* <ItemCount setCount={setCount} count={count} min={1} max={30} />
            <Button onClick={handleClickComprar}>Comprar</Button>
            <p>Precio: $ {Intl.NumberFormat().format(product.price)}</p>
            <p>Stock disponible: {product.stock}</p> */}
            <div className="row">
                <div className="col-sm">
                    <h3>{product.title}</h3>
                </div>
                <div className="col-sm">
                    <img src={product.image} alt="Foto del producto"></img>
                </div>
                <div className="col-sm">
                    <ItemCountContainer min={1} max={30} handleClick={addtocart} />
                </div>
                <div>
                    <p>Precio: $ {Intl.NumberFormat().format(product.price)}</p>
                    <p>Stock disponible: {product.stock}</p>
                </div>
                {/* <AddToCart qtyCounter={itemqty} product={product} /> */}
                <div>
                    <Button variant="dark" onClick={handleClick}>Comprar</Button>
                </div>
            </div>
        </div>
    );
}

export default ItemDetail;
