    //             <CgShoppingCart />
    //             <span className="badge white"> {cartSize()} </span>
    //         </Link>
    //     </div>
    // );
    return (
        <>
            <div classname="container">
                <Link to={'/cart'}>
                    <CgShoppingCart />
                    <span className="badge white"> {cartSize()} </span>
                </Link>
            </div>
        </>
    );
}
